            FROM AppBundle:Menu m 
            JOIN m.categorie c 
            WHERE c.nom = :nom AND m.active = :active 
            ORDER BY m.date DESC')
        ->setParameter('nom', 'VÃ©gÃ©tarien')
        ->setParameter('active', 'oui');

        $results = $query->getResult();

        return $results;
    }
}
